extras.func:
var state = global.get("state");

msg.message = "Invalid Number\n" + msg.conversation[state];

var newState = state + "." + msg.body.Body;

if (msg.body.Body === "m" || msg.body.Body === "M") {
  newState = "1"
}

if (msg.conversation[newState] != undefined) {
  global.set("state" , newState);
  msg.message = msg.conversation[newState];
}

return msg;

